needs stunnel, netcat, sed, cat, grep, tr, uniq<p>thats it<p><pre><code>       #  1.sh username &gt; 1.json
       #  1.sh &lt; 1.json &gt; 1.htm

       #!&#x2F;bin&#x2F;sh

       case $# in :)
       ;;1)
       public_ip=104.244.42.129:443;
       public_host=&quot;Host: twitter.com&quot;;
       public_token=&quot;Authorization: Bearer AAAAAAAAAAAAAAAAAAAAANRILgAAAAAAnNwIzUejRCOuH5E6I8xnZz4puTs%3D1Zv7ttfk8LF81IUq16cHjhLTvJu4FA33AGWWjCpTnA&quot;;
       printf &quot;%s\n%s\n%s\n%s\n%s\n&quot; &quot;pid=$HOME&#x2F;1.pid&quot; &quot;[ $$ ]&quot; &quot;client=yes&quot; &quot;accept=127.0.0.7:80&quot; &quot;connect=$public_ip&quot;|stunnel -fd 0;
       x=$(printf &quot;GET &#x2F; HTTP&#x2F;1.1\r\n%s\r\nConnection: close\r\n\r\n&quot; &quot;$public_host&quot;|nc -vvn 127.7 80|grep -o &quot;gt=[^;]*&quot;|sed &#x27;s&#x2F;gt=&#x2F;&#x2F;&#x27;);
       echo gt $x &gt;&#x2F;dev&#x2F;stderr;y=$(printf &quot;GET %s HTTP&#x2F;1.1\r\n%s\r\n%s\r\n%s\r\nConnection: close\r\n\r\n&quot; \
       &quot;&#x2F;i&#x2F;api&#x2F;graphql&#x2F;Vf8si2dfZ1zmah8ePYPjDQ&#x2F;UserByScreenNameWithoutResults?variables=%7B%22screen_name%22%3A%22$1%22%2C%22withHighlightedLabel%22%3Atrue%7D&quot; \
       &quot;$public_host&quot; &quot;$public_token&quot; &quot;X-Guest-Token: $x&quot;|nc -vvn 127.7 80|grep -o &quot;rest_id\&quot;:\&quot;[^\&quot;]*&quot;|sed &#x27;s&#x2F;.*\&quot;&#x2F;&#x2F;&#x27;);
       echo rest_id $y &gt;&#x2F;dev&#x2F;stderr;printf &quot;GET %s HTTP&#x2F;1.1\r\n%s\r\n%s\r\n%s\r\nConnection: close\r\n\r\n&quot; \
       &quot;&#x2F;i&#x2F;api&#x2F;graphql&#x2F;Bn2jPG0Cy8VAOakAoyV_kA&#x2F;UserTweets?variables=%7B%22userId%22%3A%22$y%22%2C%22count%22%3A500%2C%22withHighlightedLabel%22%3Atrue%2C%22withTweetQuoteCount%22%3Atrue%2C%22includePromotedContent%22%3Afalse%2C%22withTweetResult%22%3Afalse%2C%22withUserResults%22%3Afalse%2C%22withVoice%22%3Afalse%2C%22withNonLegacyCard%22%3Atrue%2C%22withBirdwatchPivots%22%3Afalse%7D&quot; &quot;$public_host&quot; &quot;$public_token&quot; &quot;X-Guest-Token: $x&quot;|nc -vvn 127.7 80
       kill -9 $(cat &quot;$HOME&quot;&#x2F;1.pid)
       ;;0)
       x=$(echo x|exec tr x &#x27;\34&#x27;);
       sed -e &#x27;s&#x2F;\\&quot;&#x2F;\&amp;quot;&#x2F;g;s&#x2F;\\n&#x2F; &#x2F;g&#x27; -e &quot;s&#x2F;\&quot;legacy\&quot;:&#x2F;$x&#x2F;g&quot; \
       |exec tr -cd &#x27;\34\11\12\40-\176&#x27;|exec tr -d &#x27;\12&#x27;|exec tr &#x27;\34&#x27; &#x27;\12&#x27; \
       |exec grep -Eo &#x27;(&quot;created_at&quot;:&quot;[^&quot;]*)|(&quot;conversation_id_str&quot;:&quot;[^&quot;]*)|(&quot;url&quot;:&quot;[^&quot;]*)|(&quot;expanded_url&quot;:&quot;[^&quot;]*)|(&quot;full_text&quot;:&quot;[^&quot;]*)&#x27; \
       |exec sed &#x27;&#x2F;&quot;created_at&quot;:&quot;&#x2F;s&#x2F;&#x2F;&lt;br&gt;&#x2F;; &#x2F;&quot;conversation_id_str&quot;:&quot;&#x2F;s&#x2F;&#x2F;&#x2F;;&#x2F;&quot;url&quot;:&quot;&#x2F;{s&#x2F;&#x2F;&#x2F;;s&#x2F;&quot;&#x2F;&#x2F;g; s&#x2F;.*&#x2F;&lt;a href=&amp;&gt;&amp;&lt;\&#x2F;a&gt;&#x2F;;};
       &#x2F;&quot;expanded_url&quot;:&quot;&#x2F;{s&#x2F;&#x2F;&#x2F;;s&#x2F;.*&#x2F;&lt;a href=&amp;&gt;&amp;&lt;\&#x2F;a&gt;&#x2F;;};&#x2F;&quot;full_text&quot;:&quot;&#x2F;s&#x2F;&#x2F;&#x2F;; s| https:&#x2F;&#x2F;[^ ]*| &lt;a href=&amp;&gt;&amp;&lt;&#x2F;a&gt; |g;s&#x2F;&gt; &#x2F;&gt;&#x2F;g;
       &#x2F;^RT @&#x2F;s&#x2F;$&#x2F; (RETWEET)&#x2F;;s&#x2F;$&#x2F;&lt;br&gt;&#x2F;;s&#x2F;\\u.\{4\}&#x2F;&#x2F;g&#x27;|uniq
       esac</code></pre>
